INSERT INTO `cms_news` VALUES ('22', 'Twist Updates #3', 'staff_party_2.png', 'New staff members ?', '<h1>Twist Updates #3</h1>\r\n<h2>There have been new staff members added to the list!</h2>\r\n<p>&nbsp;</p>\r\n<p>Everyone please welcome our new event staffs Daisy and Nes&nbsp;to the team! Along with, our new community leader being J0ker!</p>\r\n<p>Congratulations to everyone who made the team!</p>\r\n<p>&nbsp;</p>\r\n<p>Also, Congratulations to Matt&nbsp;for being promoted to Trial Moderator!</p>\r\n<p>&nbsp;</p>\r\n<p>Please stay updated on the hotel as ROTW contunies on and more users join the hotel! Apply for staff on the homepage and maybe next time around will be your chance!</p>', 'Tox', '1473045385', '1', '1', '0');
