INSERT INTO `cms_news` VALUES ('48', 'Monthly Referral Competition (December)', 'CNY_Promo.png', 'Monthly Referral Competition!', '<h1 style=\"text-align: center;\">Twist\'s Monthly Referral Competition [December]</h1>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<h3 style=\"text-align: center;\">Hey Guys, Dallas Here! I\'ve decided to give out something <span style=\"text-decoration: underline;\">Priceless</span> this month. A <span style=\"text-decoration: underline;\">Legendary Gold Angel &nbsp;Dragon</span> and 500,000 Credits! Interested in winning it? Here\'s How!</h3>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<h4 style=\"text-align: center;\">Twist will be starting a Monthly Referral Competition. Every Month the User with the most referrals registed under their Referral Link will win a One of a Kind Rare along with some credits!&nbsp;</h4>\r\n<h2 style=\"text-align: center;\">How?</h2>\r\n<h4 style=\"text-align: center;\">Easy! Give out your Twist Referral Link (Found on your homepage) to as many people as you can! Tell them about Twist and that they\'re support would help you tremendously!</h4>\r\n<h2 style=\"text-align: center;\">When?</h2>\r\n<h4 style=\"text-align: center;\">This Competition will be Monthly. Starting at the beginning of the month and ending on the very last day of the month!</h4>\r\n<h2 style=\"text-align: center;\">What Can I Win?!</h2>\r\n<h4 style=\"text-align: center;\">The Prizes Will be Different Every Month but they will be a One of a Kind Rare (Priceless) as well as 500,000 Credits!</h4>\r\n<h2 style=\"text-align: center;\">So What Are You Waiting For?! Get Started!</h2>\r\n<h3 style=\"text-align: center;\">If You Have ANY Questions Please Do Not Hesitate To Ask Staff!</h3>', 'Dallas', '1480649113', '1', '1', '0');
