INSERT INTO `cms_news` VALUES ('55', 'Twist News!', 'staff_party_2.png', 'Updates on the hotel', '<h1 style=\"text-align: center;\"><strong>Hey fellow users on Twist hotel!</strong></h1>\r\n<h3 style=\"text-align: center;\">Updates among us!<br /><br />On Twist hotel, we strive to keep everyone entertained and busy with different games, competitions, etc.. !<br /><br />Sadly, we had to remove some staff, but things will get better as we have some new additions to the staff team!<br />I\'d like everyone to give a great big welcome to our new mods and events staff!</h3>\r\n<h3 style=\"text-align: center;\">Mod-Gabe<br />Mod-Viv<br />Noelys</h3>\r\n<h3 style=\"text-align: center;\">Give these users the best of welcomes and we shall embark on a journey together to a bigger hotel! New chances at getting staff are always available so keep your eyes open!</h3>\r\n<h3 style=\"text-align: center;\">&nbsp;</h3>', 'Satan', '1484260525', '1', '1', '0');
