INSERT INTO `cms_news` VALUES ('60', 'Staff Applications are open!', 'staff_party_2.png', 'Apply Today!', '<h1 style=\"text-align: center;\"><strong>Are you looking to apply for staff?</strong></h1>\r\n<p style=\"text-align: center;\"><strong>Well, look no more! Staff applications are being released as of today! If you think you\'ve got what it takes to be staff, then please apply below!</strong></p>\r\n<p style=\"text-align: center;\"><span style=\"text-decoration: underline;\"><strong>Requirements!</strong></span></p>\r\n<p style=\"text-align: center;\">Must be at least 14 years of age!</p>\r\n<p style=\"text-align: center;\">Experience is not needed, but maturity is.</p>\r\n<p style=\"text-align: center;\">Has to at least do 10-20hrs a week on the hotel, with of the exception of family emergencies, etc</p>\r\n<h2 style=\"text-align: center;\"><em><strong><a title=\"Staff Apps\" href=\"https://goo.gl/forms/kTB0zoBwUedI3Eg22\" target=\"_blank\">Click here to apply!</a></strong></em></h2>', 'Satan', '1486841814', '1', '1', '0');
